import React, { useContext, useState } from 'react'
import { GlobalState } from '../../GlobalState'

import { Link } from 'react-router-dom'
import axios from 'axios'

function Header() {
    const state = useContext(GlobalState)
    const [isLogged] = state.userAPI.isLogged
    const [isAdmin] = state.userAPI.isAdmin
    const [cart] = state.userAPI.cart
    const [menu, setMenu] = useState(false)

    const logoutUser = async () => {
        await axios.get('/user/logout')

        localStorage.removeItem('firstLogin')

        window.location.href = "/";
    }

    const adminRouter = () => {
        return (
            <>
                <li><Link to="/create_product">Create Product</Link></li>
                <li><Link to="/category">Categories</Link></li>
            </>
        )
    }

    const loggedRouter = () => {
        return (
            <>
                <li><Link to="/history">History</Link></li>
                <li><Link to="/" onClick={logoutUser}>Logout</Link></li>
            </>
        )
    }




    return (
        <header>
            <div className="logo">

                <Link to="/">{isAdmin ? 'Admin' : 'Danfe Online Store'}</Link>

            </div>
            <div className="header_data">

                <div><Link to="/">{isAdmin ? 'Products' : 'example'}</Link></div>
                {isAdmin && adminRouter()}

                {
                    isLogged ? loggedRouter() : <div><Link to="/login"><i class="fas fa-user-alt"></i> SignIn</Link></div>
                }
                <div>

                    {
                        isAdmin ? ''
                            : <div className="cart-icon">
                                <span>{cart.length}</span>
                                <Link to="/cart">
                                    <p alt="" width="30" ><i class="fas fa-shopping-cart"></i></p>
                                </Link>
                            </div>
                    }
                </div>

            </div>

        </header>
    )
}

export default Header
