import {useState} from 'react';

const useGlobalState = () => {
    const [checkout, setCheckout] = useState({ value: '', list: [] })

    const actions = (action) => {
        const { type, payload } = action;
        switch (type) {
            case 'setCheckout':
                return setCheckout(payload);
            default:
                return checkout
        }
    }
     return { checkout, actions }
}
