import React, { useState, useRef, useEffect } from 'react';
import { SliderData } from './SliderData';
import './homepage.css';


const Homepage = () => {



    const [current, setCurrent] = useState(0)
    const length = SliderData.length
    const timeout = useRef(null)


    //for time picture change
    useEffect(() => {
        const nextSlide = () => {
            setCurrent(current == length - 1 ? 0 : current + 1)
        }
        timeout.current = setTimeout(nextSlide, 9000)

        return function () {
            if (timeout.current) {
                clearTimeout(timeout.current)
            }
        }

    }, [current, length])



    //for slider buttons to work

    const nextSlide = () => {
        setCurrent(current === length - 1 ? 0 : current + 1)

        // console.log(current)
    };

    const prevSlide = () => {
        setCurrent(current === 0 ? length - 1 : current - 1)

        //  console.log(current)
    };

    if (!Array.isArray(SliderData) || SliderData.length <= 0) {
        return null
    }




    return (
        <div className="herosection">
            <div className="herowrapper">
                {SliderData.map((SliderData, index) => {
                    return (
                        <div className="HeroSlide" key={index}>
                            {index === current && (
                                <div className="HeroSlider">
                                    <div className="HeroImage" src="https://rukminim1.flixcart.com/image/714/857/kb9ou4w0/shoe/m/s/l/tlsh210-bk-42-talaashfuro-black-original-imafsnj9qamagpkb.jpeg?q=50" alt={SliderData.alt} />
                                    <div className="HeroContent">
                                        <h1>{SliderData.title}</h1>
                                        <p>{SliderData.price}</p>
                                        < div className="Button" to={SliderData.path} primary='true' css={` max-width: 170px `}>
                                            {SliderData.label}
                                            < div className="Arrow" />
                                        </div>
                                    </div>
                                </div>

                            )}

                        </div>
                    )
                })}
                <div className="SliderButton">
                    <div onClick={prevSlide}>pervious</div>
                    <div onClick={nextSlide}>next</div>
                </div>

            </div>

        </div>
    )
}
export default Homepage
