import { useEffect, useState } from 'react'

const checkoutFromLocalStorage = JSON.parse(localStorage.getItem('checkout') || '[]');

function OrderAPI() {
    const [checkout, setCheckout] = useState({
        fullname: " checkoutFromLocalStorage.fullname",
        mobilenumber: " checkoutFromLocalStorage.mobilenumber",
        address: " checkoutFromLocalStorage.address",
    });
    const [paymentmethod, setPaymentmethod] = useState({
        paymethod: "pay on delivery ",
    });

    

    return {
        checkout: [checkout, setCheckout],
        paymentmethod: [paymentmethod, setPaymentmethod],
    }

}

export default OrderAPI
