import React, { useState, useRef, useEffect } from 'react';


export default function Homepage() {


    const Hero = ({ slides }) => {
        const [current, setCurrent] = useState(0)
        const length = slides.length
        const timeout = useRef(null)


        //for time picture change
        useEffect(() => {
            const nextSlide = () => {
                setCurrent(current == length - 1 ? 0 : current + 1)
            }
            timeout.current = setTimeout(nextSlide, 9000)

            return function () {
                if (timeout.current) {
                    clearTimeout(timeout.current)
                }
            }

        }, [current, length])



        //for slider buttons to work

        const nextSlide = () => {
            setCurrent(current === length - 1 ? 0 : current + 1)

            // console.log(current)
        };

        const prevSlide = () => {
            setCurrent(current === 0 ? length - 1 : current - 1)

            //  console.log(current)
        };

        if (!Array.isArray(slides) || slides.length <= 0) {
            return null
        }




        return (
            <div className="herosection">
                <div className="herowrapper">
                    {slides.map((slide, index) => {
                        return (
                            <div className="HeroSlide" key={index}>
                                {index === current && (
                                    <div className="HeroSlider">
                                        <div className="HeroImage" src={slide.images} alt={slide.alt} />
                                        <div className="HeroContent">
                                            <h1>{slide.title}</h1>
                                            <p>{slide.price}</p>
                                            < div className="Button" to={slide.path} primary='true' css={` max-width: 170px `}>
                                                {slide.label}
                                                < div className="Arrow" />
                                            </div>
                                        </div>
                                    </div>

                                )}

                            </div>
                        )
                    })}
                    <div className="SliderButton">
                        <div onClick={prevSlide}>pervious</div>
                        <div onClick={nextSlide}>next</div>
                    </div>

                </div>

            </div>
        )
    }
}
