import React, { useContext } from 'react'
import { GlobalState } from '../../../GlobalState'
import CheckoutSteps from '../checkoutsteps/CheckoutSteps'


function Order() {

    const state = useContext(GlobalState)
    const [cart, setCart] = state.cartAPI.cart
    const [total, setTotal] = useState(0)

    useEffect(() => {
        const getTotal = () => {
            const total = cart.reduce((prev, item) => {
                return prev + (item.price * item.quantity)
            }, 0)

            setTotal(total)
        }

        getTotal()

    }, [cart])

    const addToCart = async (cart) => {
        await axios.patch('/user/addcart', { cart }, {
            headers: { Authorization: token }
        })
    }


    const increment = (id) => {
        cart.forEach(item => {
            if (item._id === id) {
                item.quantity += 1
            }
        })

        setCart([...cart])
        addToCart(cart)
    }

    const decrement = (id) => {
        cart.forEach(item => {
            if (item._id === id) {
                item.quantity === 1 ? item.quantity = 1 : item.quantity -= 1
            }
        })

        setCart([...cart])
        addToCart(cart)
    }

    const removeProduct = id => {
        if (window.confirm("Do you want to delete this product?")) {
            cart.forEach((item, index) => {
                if (item._id === id) {
                    cart.splice(index, 1)
                }
            })

            setCart([...cart])
            addToCart(cart)
        }
    }
    return (
        <>

            <CheckoutSteps step1 step2 step3 ></CheckoutSteps>
            <div className="order-summary">

                <div className="column-1">
                    <h2>Shipping Details</h2>
                    <div>	<h4>Name:</h4> User Full Name </div>
                    <div><h4>Address:</h4> User address </div>
                    <div>	<h4>Payment Method:</h4> Pay On Delivery </div>
                    <div>	<h3>Order Items:</h3> </div>
                    {cart.map(product => (
                        <div>
                            <img src={product.images.url} alt="image" className="smallimage" ></img>
                            <h5>{product.title}</h5>
                            <h5>Rs.{product.price}</h5>
                        </div>
                    ))}

                </div>
                <div className="column-2">
                    <h2>Order Summary</h2>
                    <div> <h4>Net price</h4> Rs. </div>
                    <div>	<h4>Shipping Cost </h4> Rs. price ( if your order is inside valley, shiiping will be free) </div>
                    <div>	<h4>Total Cost </h4> Rs. total price /- </div>
                    <button>Place an Order</button>
                </div>
            </div>

        </>

    )
}

export default Order
